package registroapp1;

import org.junit.jupiter.api.Test;
import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.DisplayName;
import static org.junit.jupiter.api.Assertions.*;

/**
 * Tests para la aplicación de registro
 */
@DisplayName("Pruebas de RegistroApp")
class AppTest {
    
    private App app;
    private RegistroApp registroApp;
    
    @BeforeEach
    void setUp() {
        app = new App();
        registroApp = new RegistroApp();
    }
    
    @Test
    @DisplayName("La aplicación principal debe poder instanciarse")
    void appCanBeInstantiated() {
        assertNotNull(app, "La aplicación debería poder instanciarse");
    }
    
    @Test
    @DisplayName("RegistroApp debe poder instanciarse")
    void registroAppCanBeInstantiated() {
        assertNotNull(registroApp, "RegistroApp debería poder instanciarse");
    }
    
    @Test
    @DisplayName("Los métodos de logging deberían ejecutarse correctamente")
    void loggingMethodsShouldExecuteCorrectly() {
        assertDoesNotThrow(() -> {
            registroApp.demostrarPatronesLogging();
        }, "demostrarPatronesLogging no debería lanzar excepciones");
        
        assertDoesNotThrow(() -> {
            registroApp.simularErrores();
        }, "simularErrores no debería lanzar excepciones");
    }
    
    @Test
    @DisplayName("ejecutarOperaciones debería manejar excepciones internamente")
    void ejecutarOperacionesShouldHandleExceptions() {
        // Este método maneja excepciones internamente y no debería propagarlas
        assertDoesNotThrow(() -> {
            registroApp.ejecutarOperaciones();
        }, "ejecutarOperaciones debería manejar las excepciones internamente");
    }
}
